import React, { useState, useEffect } from 'react';
import './TypingAnimation.css';

const TypingAnimation = ({ text, speed }) => {
  const [lines, setLines] = useState([]);
  const [currentLineIndex, setCurrentLineIndex] = useState(0);
  const [currentLine, setCurrentLine] = useState('');

  useEffect(() => {
    setLines(text.split('\n'));

    const lineIntervalId = setInterval(() => {
      if (currentLine.length < lines[currentLineIndex].length) {
        setCurrentLine((prevLine) => prevLine + lines[currentLineIndex][currentLine.length]);
      } else {
        clearInterval(lineIntervalId);
        setTimeout(() => {
          setCurrentLine('');
          setCurrentLineIndex((prevIndex) => prevIndex + 1);
        }, speed); // Add a delay before moving to the next line
      }
    }, speed);

    return () => clearInterval(lineIntervalId);
  }, [text, speed, currentLine, lines, currentLineIndex]);

  return (
    <div className="typing-animation">
      {lines.map((line, index) => (
        <div key={index}>{index === currentLineIndex ? currentLine : line}</div>
      ))}
    </div>
  );
};

export default TypingAnimation;
